<style scoped>
.nitro-octane-logo {
  opacity: 0;
  animation: fade-in 1s forwards;
}

#flame {
  fill: url(#logoGradient);
  transition: fill 0.3s ease-in-out;
  animation: flame-gradient-move 3s linear infinite;
}

@keyframes flame-gradient-move {
  0% {
    filter: hue-rotate(0deg);
  }
  25% {
    filter: hue-rotate(-60deg);
  }
  50% {
    filter: hue-rotate(60deg);
  }
  75% {
    filter: hue-rotate(-30deg);
  }
  100% {
    filter: hue-rotate(0deg);
  }
}

#nitro-octane-logo stop {
  animation: gradient-stop-flicker 1.3s infinite alternate-reverse;
}

#nitro-octane-logo stop[offset='0%'] {
  animation-delay: 0s;
}
#nitro-octane-logo stop[offset='50%'] {
  animation-delay: 0.5s;
}
#nitro-octane-logo stop[offset='100%'] {
  animation-delay: 1s;
}

@keyframes gradient-stop-flicker {
  0% {
    stop-color: yellow;
  }
  25% {
    stop-color: #ffe066;
  }
  50% {
    stop-color: orange;
  }
  75% {
    stop-color: #ff6600;
  }
  100% {
